export  function  unmount ( props )  { ... } 
export  const  timeouts =  { 
  bootstrap:  { 
    millis:  5000 , 
    dieOnTimeout:  true , 
    warningMillis:  2500 , 
  } , 
  mount:  { 
    millis:  5000 , 
    dieOnTimeout:  false , 
    warningMillis:  2500 , 
  } , 
  unmount:  { 
    millis:  5000 , 
    dieOnTimeout:  true , 
    warningMillis:  2500 , 
  } , 
  unload:  { 
    millis:  5000 , 
    dieOnTimeout:  true , 
    warningMillis:  2500 , 
  } , 
} ; 





