// app.widget.js 
export  default  ( )  =>  ( { 
  async  mount ( {  container }  )  { 
    container. innerHTML  =  ` 
      &#x3C; style >  
        :host ( .you-theme )  h3  { 
          color :  #FFF ; 
          background :  #000 ; 
        } 
        &#x3C;/ style >  
      &#x3C; h3 >  hello world&#x3C;/ h3 >  
     `  ; 
  } , 

  async  unmount ( {  container }  )  { 
    container. innerHTML  =  '' ; 
  } 
} ) ;

